import React from 'react'
import './App.css'

// Components
import Home  from './Home/Home'
import Product from './Products/Products'
import About from './About/About'
import NavBarScrolling from './NavBarScrolling/NavBarScrolling'

// Tools Used
import { isBrowser, isMobile } from 'react-device-detect'

type LinkValue = {
  name: string;
  path: string;
  mobileAdjustment: number;
  browserAdjustment: number;
}

const App: React.FC = () => {
  // Links in Nav Bar
  // Mobile & Browser adjustments measured in px.
  const links: LinkValue[] = [
      { name: 'Home', path: 'Home', mobileAdjustment: 0, browserAdjustment: 0 },
      { name: 'About', path: 'lynette-photo', mobileAdjustment: -100, browserAdjustment: -180 },
      { name: 'Services', path: 'Services', mobileAdjustment: 0, browserAdjustment: 0 },
      { name: 'Products', path: 'Products', mobileAdjustment: 0, browserAdjustment: 0 },
      { name: 'Contact', path: 'Contact', mobileAdjustment: 0, browserAdjustment: 0 },
  ]

  const scrollToPage = (id: string, mobileAdjustment: number, browserAdjustment: number): void => {
    const element = document.getElementById(id)
    if (element) {
        var top = element.getBoundingClientRect().top + window.pageYOffset

        // When on Mobile, scroll down to have the content just below the 
        // mobile nav. bar
        if (isMobile)
          top += mobileAdjustment
        else if (isBrowser)
          top += browserAdjustment

        window.scrollTo({ top })
    }
  }

  const getPageBorders = (): Map<string, number> => {
    var pageEntryBorders: Map<string, number>

    for (let i = 0; i < links.length; i++) {
        const pageName: string = links[i].name
        const element = document.getElementById(pageName)

        if (element) {
            var topOfPage = element.getBoundingClientRect().top + window.pageYOffset

            pageEntryBorders.set(pageName, topOfPage)
        }
    }

    return pageEntryBorders
  }

  // Gets distance from the top of the website to each page (in px.)
  // const pageEntryBorders: Map<string, number> = getPageBorders()
  console.log()

  return (
    <div className='App'>
      <NavBarScrolling 
        links={ links } 
        scrollToPage={ scrollToPage } />
      <Home 
        links={ links } 
        scrollToPage={ scrollToPage }/>
      <About />
      <Product />
    </div>
  )
}

export default App
